///Función que muestra por pantalla un listado de productos.
//http://localhost/ra1clienteservidorexmamen/servidor/api.php?modo=texto
function mostrarProductosJSON($productos) {
    echo "--- Lista de productos ---<br>";
    $json = json_encode($productos);
    $array = json_decode($json, true);
    //Crear un foreach para recorrerlo  y pintar por pantalla, el id, nombre y precio del producto
 // var_dump($array);
  foreach ($array as $producto) {
    echo "<div class='producto'>";
    echo "<h2>" . htmlspecialchars($producto['nombre']) . "</h2>";
    echo "<p>" . htmlspecialchars($producto['descripcion']) . "</p>";
    echo "<p>Precio: $" . number_format($producto['precio'], 2) . "</p>";
    echo "<p>Categoría: " . htmlspecialchars($producto['categoria']) . "</p>";
    // Aquí puedes usar la imagen si tienes una URL válida
    echo "<img src='" . htmlspecialchars($producto['img']) . "' alt='" . htmlspecialchars($producto['nombre']) . "'>";
    echo "</div>";
}



